import React, { Component } from "react";
import Grid from "@material-ui/core/Grid";
import Avatar from "@material-ui/core/Avatar";
import Typography from "@material-ui/core/Typography";

import ExpansionPanel from "@material-ui/core/ExpansionPanel";
import ExpansionPanelSummary from "@material-ui/core/ExpansionPanelSummary";
import ExpansionPanelDetails from "@material-ui/core/ExpansionPanelDetails";
import ExpandMoreIcon from "@material-ui/icons/ExpandMore";
import Button from "@material-ui/core/Button";
import Divider from "@material-ui/core/Divider";
import ExpansionPanelActions from "@material-ui/core/ExpansionPanelActions";

import Card from "@material-ui/core/Card";
import CardActions from "@material-ui/core/CardActions";
import CardContent from "@material-ui/core/CardContent";
import CardMedia from "@material-ui/core/CardMedia";

import "./App.css";
import { players } from "./constants/players.js";

class DraftingPage extends Component {
  state = {
    draftedPlayers: ["Lebron James", "Stephen Curry"],
    playerLists: [],
    userProfile: []
  };

  handleClick = e => {
    console.log(e);
    this.setState({
      draftedPlayers: this.state.draftedPlayers.append(e)
    });
  };

  initialize = this.initialize.bind(this);

  initialize() {
    /*
    let drafted = [];
    drafted.push(
      <p style={{ fontSize: 50, color: "blue" }}>Currently Drafted</p>
    );
    this.props.draftedPlayers.forEach(player => {
      drafted.push(<img className="ProfilePic" src={player[1]} alt="logo" />);
      drafted.push(<p>{player[0]}</p>);
    });*/
    /*

    let players = [];
    // players.push(<p style={{ fontSize: 50, color: "orangered" }}>Players</p>);
    // this.props.playerLists.forEach(player => {
    //   players.push(<p>{player}</p>)
    // });
    let length = this.props.playerLists.length;
    if (length % 2 === 0) {
      for (let i = 0; i < length; i += 2) {
        players.push(
          <Grid container direction="row" justify="space-evenly">
            <div>
              <img
                className="ProfilePic"
                src={this.props.playerLists[i][1]}
                alt="logo"
              />
              <p>{this.props.playerLists[i][0]}</p>
            </div>
            <div>
              <img
                className="ProfilePic"
                src={this.props.playerLists[i + 1][1]}
                alt="logo"
              />
              <p>{this.props.playerLists[i + 1][0]}</p>
            </div>
          </Grid>
        );
      }
    }

    this.setState({
      draftedPlayers: drafted,
      userProfile: profile,
      playerLists: players
    });
   */
  }

  componentDidMount() {
    this.initialize();
  }

  render() {
    return (
      <Grid
        container
        direction="row"
        alignItems="stretch"
        justify="space-evenly"
      >
        <div>
          <p>hi</p>
        </div>

        <div>
          <Grid container direction="row">
            <div style={{ textAlign: "center", color: "orangered" }}>
              <h1>Draft Players</h1>
              <div>
                {players.map(p => {
                  return (
                    <ExpansionPanel>
                      <ExpansionPanelSummary expandIcon={<ExpandMoreIcon />}>
                        <Avatar src={p.img} />

                        <Typography>{p.name} </Typography>
                      </ExpansionPanelSummary>
                      <ExpansionPanelDetails>
                        <Typography>
                          Lorem ipsum dolor sit amet, consectetur adipiscing
                          elit. Suspendisse malesuada lacus ex, sit amet blandit
                          leo lobortis eget.
                        </Typography>
                      </ExpansionPanelDetails>
                      <Divider />
                      <ExpansionPanelActions>
                        <Button size="small">Learn More</Button>
                        <Button
                          size="small"
                          color="primary"
                          variant="contained"
                          onClick={() => this.handleClick(p.name)}
                        >
                          Draft
                        </Button>
                      </ExpansionPanelActions>
                    </ExpansionPanel>
                  );
                })}
              </div>
            </div>
          </Grid>
        </div>

        <div>
          <Grid container direction="column" justify="space-evenly">
            <div style={{ textAlign: "center" }}>
              <h1>Your Players</h1>
              {this.state.draftedPlayers.map(drafted => {
                const playerInfo = players.find(
                  player => player.name == drafted
                );

                return (
                  <Card style={{ marginTop: 10 }}>
                    <div
                      style={{ textAlign: "center", display: "inline-block" }}
                    >
                      <Avatar
                        src={playerInfo.img}
                        style={{
                          width: 60,
                          height: 60
                        }}
                      />
                    </div>
                    <CardContent>
                      <Typography color="textSecondary">
                        {playerInfo.name}
                      </Typography>
                    </CardContent>
                    <CardActions
                      style={{ textAlign: "center", display: "inline-block" }}
                    >
                      <Button
                        size="small"
                        color="secondary"
                        variant="contained"
                      >
                        Delete
                      </Button>
                    </CardActions>
                  </Card>
                );
              })}
            </div>
          </Grid>
        </div>
      </Grid>
    );
  }
}

export default DraftingPage;
