import React, { Component } from "react";
import Grid from "@material-ui/core/Grid";
import Avatar from "@material-ui/core/Avatar";
import Typography from "@material-ui/core/Typography";

import ExpansionPanel from "@material-ui/core/ExpansionPanel";
import ExpansionPanelSummary from "@material-ui/core/ExpansionPanelSummary";
import ExpansionPanelDetails from "@material-ui/core/ExpansionPanelDetails";
import ExpandMoreIcon from "@material-ui/icons/ExpandMore";
import Button from "@material-ui/core/Button";
import Divider from "@material-ui/core/Divider";
import ExpansionPanelActions from "@material-ui/core/ExpansionPanelActions";

import Card from "@material-ui/core/Card";
import CardActions from "@material-ui/core/CardActions";
import CardContent from "@material-ui/core/CardContent";
import CardMedia from "@material-ui/core/CardMedia";

import "./App.css";
import { players } from "./constants/players.js";

class DraftingPage extends Component {
  state = {
    draftedPlayers: []
  };

  handleClick = e => {
    const newPlayers = this.state.draftedPlayers.push(e);

    console.log(newPlayers);

    this.setState({
      draftedPlayers: newPlayers
    });
  };

  render() {
    return (
      <Grid
        container
        direction="row"
        alignItems="stretch"
        justify="space-evenly"
      >
        <div>
          <p>hi</p>
        </div>

        <div>
          <Grid container direction="row">
            <div style={{ textAlign: "center", color: "orangered" }}>
              <h1>Draft Players</h1>
              <div>
                {players.map(p => {
                  return (
                    <ExpansionPanel>
                      <ExpansionPanelSummary expandIcon={<ExpandMoreIcon />}>
                        <Avatar src={p.img} />

                        <Typography>{p.name} </Typography>
                      </ExpansionPanelSummary>
                      <ExpansionPanelDetails>
                        <Typography>
                          Lorem ipsum dolor sit amet, consectetur adipiscing
                          elit. Suspendisse malesuada lacus ex, sit amet blandit
                          leo lobortis eget.
                        </Typography>
                      </ExpansionPanelDetails>
                      <Divider />
                      <ExpansionPanelActions>
                        <Button size="small">Learn More</Button>
                        <Button
                          size="small"
                          color="primary"
                          variant="contained"
                          onClick={() => this.handleClick(p.name)}
                        >
                          Draft
                        </Button>
                      </ExpansionPanelActions>
                    </ExpansionPanel>
                  );
                })}
              </div>
            </div>
          </Grid>
        </div>

        <div>
          <Grid container direction="column" justify="space-evenly">
            <div style={{ textAlign: "center" }}>
              <h1>Your Players</h1>

              <p>{this.state.draftedPlayers[0]}</p>
            </div>
          </Grid>
        </div>
      </Grid>
    );
  }
}

export default DraftingPage;
