import React, { Component } from "react";
import { players } from "./constants/players.js";

import deepOrange from '@material-ui/core/colors/deepOrange';
import Grid from '@material-ui/core/Grid';
import Avatar from '@material-ui/core/Avatar';
import Typography from "@material-ui/core/Typography";
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemText from '@material-ui/core/ListItemText';
import ListItemAvatar from '@material-ui/core/ListItemAvatar';
import { Divider } from "@material-ui/core";

import "./App.css";
import { stat } from "fs";

const styles = {
  avatar: {
    margin: 50,
    width: 300,
    height: 300,
  },
  title: {
    paddingTop: 50,
    paddingBottom: 50
  },
  wordAvatar: {
    width: 100,
    height: 100,
    margin: 50,
    fontSize: 30,
    color: '#000080',
    backgroundColor: deepOrange[500],
  },
  text: {
    paddingTop: 25,
    paddingBottom: 25
  }
};

const basicInfo = {
  "Position": "S.F.",
  "Age": 34,
  "Team": "LAL",
}

const gameStats = {
  "G": 39,
  "GS": 39,
  "MPG": 34.9,
}

const stats = {
  "FG": 9.8,
  "FGA": 19.2,
  "FG%": 0.513,
  "3P": 2.1,
  "3PA": 5.8,
  "3P%": 0.355,
  "2P": 7.7,
  "2PA": 13.3,
  "2P%": 0.582,
  "eFG%": 0.567,
  "FT": 5.1,
  "FTA": 7.4,
  "FT%": 0.679,
  "ORB": 0.9,
  "DRB": 7.7,
  "TRB": 8.6,
  "AST": 7.6,
  "STL": 1.3,
  "BLK": 0.6,
  "TOV": 3.5,
  "PF": 1.7,
  "PS/G": 26.8
}

function PlayerInfoPage(props) {
  let statistics1 = [];
  let statistics2 = [];
  let statKeys = Object.keys(stats);
  let halfLength = statKeys.length / 2;
  for (let i = 0; i < halfLength; i++) {
    statistics1.push(
      <ListItem>
        <ListItemAvatar>
          <Avatar style={styles.wordAvatar}>
            {statKeys[i]}
          </Avatar>
        </ListItemAvatar>
        <Typography variant="h3" style={styles.title}>{stats[statKeys[i]]}</Typography>
      </ListItem >
    );
    statistics2.push(
      <ListItem>
        <ListItemAvatar>
          <Avatar style={styles.wordAvatar}>
            {statKeys[i + halfLength]}
          </Avatar>
        </ListItemAvatar>
        <Typography variant="h3" style={styles.title}>{stats[statKeys[i + halfLength]]}</Typography>
      </ListItem >
    );
  }
  // for (let key in stats) {
  //   statistics.push(
  //     <ListItem>
  //       <ListItemAvatar>
  //         <Avatar style={styles.wordAvatar}>
  //           {key}
  //         </Avatar>
  //       </ListItemAvatar>
  //       <Typography variant="h3" style={styles.title}>{stats[key]}</Typography>
  //     </ListItem>
  //   );
  // }

  return (
    <Grid container direction="column">
      <Avatar src={players[0].img} style={styles.avatar} />
      <Typography variant="h1" style={styles.title}>{players[0].name}</Typography>
      <Typography variant="h3" style={styles.title}>Basic Info</Typography>
      <List>
        <ListItem>
          <ListItemAvatar>
            <Avatar style={styles.wordAvatar}>
              Pos
            </Avatar>
          </ListItemAvatar>
          <Typography variant="h3" style={styles.title}>{basicInfo.Position}</Typography>
          <ListItemAvatar>
            <Avatar style={styles.wordAvatar}>
              Age
            </Avatar>
          </ListItemAvatar>
          <Typography variant="h3" style={styles.title}>{basicInfo.Age}</Typography>
          <ListItemAvatar>
            <Avatar style={styles.wordAvatar}>
              Team
            </Avatar>
          </ListItemAvatar>
          <Typography variant="h3" style={styles.title}>{basicInfo.Team}</Typography>
        </ListItem>
        <ListItem>
          <ListItemAvatar>
            <Avatar style={styles.wordAvatar}>
              G
            </Avatar>
          </ListItemAvatar>
          <Typography variant="h3" style={styles.title}>{gameStats.G}</Typography>
          <ListItemAvatar>
            <Avatar style={styles.wordAvatar}>
              GS
            </Avatar>
          </ListItemAvatar>
          <Typography variant="h3" style={styles.title}>{gameStats.GS}</Typography>
          <ListItemAvatar>
            <Avatar style={styles.wordAvatar}>
              MPG
            </Avatar>
          </ListItemAvatar>
          <Typography variant="h3" style={styles.title}>{gameStats.MPG}</Typography>
        </ListItem>
      </List>
      <Typography variant="h3" style={styles.title}>Statistics</Typography>
      <Grid container direction="row">
        <List>
          {statistics1}
        </List>
        <List>
          {statistics2}
        </List>
      </Grid>
    </Grid>
  );
}

