import React, { Component } from "react";
import axios from "axios";

import { players } from "./constants/players.js";

import Grid from "@material-ui/core/Grid";
import Typography from "@material-ui/core/Typography";
import Avatar from "@material-ui/core/Avatar";

import Card from "@material-ui/core/Card";
import CardActionArea from "@material-ui/core/CardActionArea";
import CardContent from "@material-ui/core/CardContent";
import CardMedia from "@material-ui/core/CardMedia";

const styles = {
  avatar: {
    margin: 50,
    width: 150,
    height: 150
  }
};

class Profile extends Component {
  constructor(props) {
    super(props);
    this.state = {
      draftedPlayers: []
    };
  }

  componentDidMount() {
    const requestUrl =
      "http://127.0.0.1:5000/get_team?user=" + this.props.user.name;
    axios.get(requestUrl).then(response => {
      console.log(response);
      this.setState({
        draftedPlayers: response.data
      });
    });
  }
  render() {
    return (
      <Grid container direction="column" justify="center" alignItems="center">
        <Avatar
          alt={this.props.user.name}
          src={this.props.user.imageUrl}
          style={styles.avatar}
        />
        <Typography variant="h1">{this.props.user.name}</Typography>
        <Typography variant="h2">Your team</Typography>
        <Card>
          <CardActionArea>
            <CardMedia src={players[0].info.img} />
            <CardContent>
              <Avatar src={players[0].info.img} />
              <Typography gutterBottom variant="h5" component="h2">
                {this.state.draftedPlayers[0]}
              </Typography>
            </CardContent>
          </CardActionArea>
        </Card>
      </Grid>
    );
  }
}

export default Profile;
