import React, { Component } from "react";
import { players } from "./constants/players.js";
import Avatar from '@material-ui/core/Avatar';
import Typography from "@material-ui/core/Typography";


import "./App.css";

const styles = {
  avatar: {
    margin: 10,
  },
  bigAvatar: {
    margin: 10,
    width: 60,
    height: 60,
  },
};

const titleStyle = {
  paddingTop: 100,
  paddingBottom: 100
};

function PlayerInfoPage(props) {
  const { classes } = props;
  return (
    <div>
      <Avatar src={players[0].img} className={classes.avatar} />
      <Typography variant="h1" style={titleStyle}>{players[0].name}</Typography>
    </div>
  );
}

PlayerInfoPage.propTypes = {
  classes: PropTypes.object.isRequired,
};
